INSERT INTO `wp_posts` VALUES("5", "1", "2015-02-17 11:20:31", "2015-02-17 11:20:31", "[vegas id=67 fade=3000 delay=4000 overlay= arrows=yes autoplay=yes poster=no]\r\n\r\n[ezcol_1quarter]\r\n<h5 style=\"padding-left: 30px;\">Welcome to Nicky Murray Design</h5>\r\n<p style=\"padding-left: 30px;\"><strong><img class=\"alignnone size-full wp-image-223\" src=\"http://welivedthedream.com/dev/wp-content/uploads/2015/02/divider_2.jpg\" alt=\"divider_2\" width=\"800\" height=\"20\" /></strong></p>\r\n<p style=\"padding-left: 30px;\">Nicky Murray Design is a luxury specialist interior designer who creates bespoke inspirational interiors for both residential and commercial clients. We have a wealth of knowledge and experience providing an exclusive yet personal service. Nicky and her team help to create a smooth passage from initial concept through to project delivery, always aiming to exceed her clients expectations.</p>\r\n<p style=\"padding-left: 30px;\">Experienced with both residential and commercial properties internationally, the London-based designer draws inspiration from all aspects of life from her exotic travels to attending fashion and trade shows. With an aptitude for “getting inside her clients’ heads,” the intrepid adventurer consistently delivers extraordinary interiors that dare to be different.</p>\r\n<p style=\"padding-left: 30px;\"><img class=\"alignnone size-full wp-image-223\" src=\"http://welivedthedream.com/dev/wp-content/uploads/2015/02/divider_2.jpg\" alt=\"divider_2\" width=\"800\" height=\"20\" /></p>\r\n[/ezcol_1quarter] [ezcol_3quarter_end][/ezcol_3quarter_end]", "", "", "publish", "closed", "closed", "", "home", "", "", "2015-12-02 10:49:24", "2015-12-02 10:49:24", "", "0", "http://welivedthedream.com/dev/?page_id=5", "0", "page", "", "0");
