INSERT INTO `wp_posts` VALUES("72", "1", "2015-02-25 11:09:23", "2015-02-25 11:09:23", "<p style=\"padding-left: 30px;\">[ezcol_1quarter]</p>\r\n<p style=\"padding-left: 30px;\"><img class=\"alignnone size-full wp-image-223\" src=\"http://welivedthedream.com/dev/wp-content/uploads/2015/02/divider_2.jpg\" alt=\"divider_2\" width=\"800\" height=\"20\" /></p>\r\n<p style=\"padding-left: 30px;\">Our offices are open between 9am and 5pm, Monday to Friday. If you would like to arrange an appointment, please get in touch via the contact form provided.</p>\r\n<p style=\"padding-left: 30px;\">6-8 Sackville Street\r\nLondon\r\nW1S 3DG\r\n+44 (0) 203 053 9991</p>\r\n<p style=\"padding-left: 30px;\">45 Charlotte Square\r\nEdinburgh\r\nEH2 4HQ\r\n+44 (0)131 510 0289</p>\r\n<p style=\"padding-left: 30px;\">M: 07788 420199\r\nE: <a href=\"http://info@nickymurraydesign.com\">info@nickymurraydesign.com</a></p>\r\n\r\n\r\n<hr style=\"padding-left: 30px;\" />\r\n<p style=\"padding-left: 30px;\">We are always interested in hearing from talented, dedicated and enthusiastic designers. Please send your CV and two projects case studies demonstrating your skills and experience to: <a href=\"http://info@nickmurraydesign.com\">info@nickymurraydesign.com</a></p>\r\n[/ezcol_1quarter] [ezcol_1quarter]\r\n\r\n<img class=\"alignnone size-full wp-image-223\" src=\"http://welivedthedream.com/dev/wp-content/uploads/2015/02/divider_2.jpg\" alt=\"divider_2\" width=\"800\" height=\"20\" />\r\n<p style=\"padding-left: 30px;\">[contact-form-7 id=\"95\" title=\"Contact form 1\"]</p>\r\n[/ezcol_1quarter] [ezcol_1half_end]<a href=\"http://www.nickymurraydesign.com/wp-content/uploads/2015/02/interior_shot.jpg\"><img class=\"alignright size-full wp-image-455\" src=\"http://www.nickymurraydesign.com/wp-content/uploads/2015/02/interior_shot.jpg\" alt=\"interior_shot\" width=\"500\" height=\"900\" /></a>[/ezcol_1half_end]", "", "", "publish", "closed", "closed", "", "contact", "", "", "2015-12-10 11:23:24", "2015